import React,{Component} from 'react';
class StaffAdd extends Component
{
	
	state=
	{
		   
		   staffs: [],
		   result:""
	}
	  
	  handleAdd = (e) =>
	  {
		  let staff = {id:0,num:0,type:"",shift:""};
		  let staffs=[...this.state.staffs,staff]
		  this.setState({staffs:staffs})
	  }
	 addNum = (id) =>
	 {
	 	 let staffs=this.state.staffs;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
	 		 if(staffs[i].id===id)
	 		 {
	 			 staffs[i].num++;
	 		 }
	 	 	
	 	 }
	 	 this.setState({staffs:staffs});
		 setResult(staffs);
	 }
	 reduceNum = (id) =>
	 {
		 
	 	 let staffs=this.state.staffs;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
	 		 if(staffs[i].id===id)
	 		 {
	 			 if(staffs[i].num>0)
	 			 {
	 				  staffs[i].num--;
	 			 }
	 			
	 		 }
	 	 	
	 	 }
	 	 this.setState({staffs:staffs});
		 setResult(staffs);
	 }
	
	setResult = (staffs) =>
	{
			 
		 for(var i=0;i<staffs.length;i++)
		 {
			let num = 0;
			for(var i=0;i<staffs.length;i++)
			{
				num = num+staffs[i].num;
			}
		 	
		 }
		 const result = this.props.result;
		 aleret(result)
		
	}
	
	  render () {
		const staffList = this.state.staffs.map((staff) =>
		    <div key={staff.id}>
		      <div onClick={() => this.addNum(staff.id)} >+</div> <div  onClick={() => this.reduceNum(staff.id)}>-</div><input  />
			  <div>type
			  <select>
				<option>RN</option>
				<option>LVN</option>
			   <option>Unlicensed</option>
			   </select>
			  </div>
			  <div>shift
			  <select>
			  <option>12 Hours Day</option>
			  <option>12 Hours Night</option>
			   <option>8 Hours Day</option>
			   <option>8 Hours Evening</option>
			   <option>8 Hours Night</option>
			   </select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleAdd}>Add new Staff</button></div>
				<div>{staffList}</div>
				<div>{this.state.result}</div>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
