import React,{Component,useState } from 'react';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
class StaffAdd extends Component
{
	
	state=
	{
		   
		   staffs: [],
		   results:"",
		   open:false,
		   style : {
		     position: 'absolute',
		     top: '50%',
		     left: '50%',
		     transform: 'translate(-50%, -50%)',
		     width: 400,
		     bgcolor: 'background.paper',
		     border: '2px solid #000',
		     boxShadow: 24,
		     p: 4,
		   }
	}
	  
	  handleAdd = (e) =>
	  {
		  let staff = {id:Math.random(),num:1,type:"",shift:""};
		  let staffs=[...this.state.staffs,staff]
		  this.setState({staffs:staffs})
	  }
	 addNum = (id) =>
	 {
	 	 let staffs=this.state.staffs;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
	 		 if(staffs[i].id===id)
	 		 {
	 			 staffs[i].num++;
	 		 }
	 	 	
	 	 }
	 	 this.setState({staffs:staffs});
		 this.setResult(staffs);
	 }
	 reduceNum = (id) =>
	 {
		 
	 	 let staffs=this.state.staffs;
		 staffs.filter(item => item.id===id )
		 let isDelete=false;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
			 debugger;
	 		 if(staffs[i].id===id)
	 		 {
	 			 if(staffs[i].num>1)
	 			 {
	 				  staffs[i].num--;
					  
	 			 }else
				 {
					 	isDelete=true;  
				 }
	 			break;
	 		 }
	 	 	
	 	 }
		 if(isDelete)
		 {
			  staffs = staffs.filter(item => item.id!==id )
		 }
		 
	 	 this.setState({staffs:staffs});
		this.setResult(staffs);
	 }
	
	setResult = (staffs) =>
	{
			 
		 let num = 0;
		 for(var i=0;i<staffs.length;i++)
		 {
			
			num = num+staffs[i].num;
			
		 }
		 const result = this.props.result;
		 if(result===''){
			 return;
		 }
		 if(num<=result)
		 {
			 let show = num+"\n\r"+"In line with budget";
			 this.setState({"results":show});
		 }else
		 {
			 let show = num+"\n\r"+"The rated budget has been exceeded";
			 this.setState({"results":show});
		 }
		
	}
	

	
	handleOpen = () =>
	{
			 
		this.setState({"open":true});	
	}
	handleClose = () =>
	{
			 
		this.setState({"open":false});	
	}
	// const [open, setOpen] = React.useState(false);
	
	  render () {
		const staffList = this.state.staffs.map((staff) =>
		    <div key={staff.id}>
		      <div onClick={() => this.addNum(staff.id)} >+</div> <div  onClick={() => this.reduceNum(staff.id)}>-</div><input value={staff.num} />
			  <div>type
			  <select>
				<option>RN</option>
				<option>LVN</option>
			   <option>Unlicensed</option>
			   </select>
			  </div>
			  <div>shift
			  <select>
			  <option>12 Hours Day</option>
			  <option>12 Hours Night</option>
			   <option>8 Hours Day</option>
			   <option>8 Hours Evening</option>
			   <option>8 Hours Night</option>
			   </select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleOpen}>Add new Staff</button></div>
				<div>{staffList}</div>
				<div>{this.state.results}</div>
				
				
				
				<Modal
				        open={this.state.open}
				        onClose={this.handleClose}
				        aria-labelledby="modal-modal-title"
				        aria-describedby="modal-modal-description"
				      >
				        <Box sx={this.state.style}>
				          <Typography id="modal-modal-title" variant="h6" component="h2">
				            Text in a modal
				          </Typography>
				          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
				            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
				          </Typography>
				        </Box>
				</Modal>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
