import React, { Component } from 'react';
import StaffAdd from './StaffAdd'
import Scenario from './Scenario'

class Main extends Component {
    state =
        {
            title: "BSU In-Patient Nurse Staffing Simulator",
            results: "",
            staffNum: "The Results:",
            num: "",
            staffs: [],
            Scenario: {
                unit: "",
                HPPD: "",
                bedUnit: "",
                census: "",
            }
        }

    calculation = (Scenario, num) => {

        if (Scenario.bedUnit !== '' && Scenario.census !== '' && Scenario.HPPD !== '' && num !== '') {
            let result = (Scenario.bedUnit * (Scenario.census / 100)) * Scenario.HPPD / 12;
            console.log(num + ":" + result)
            if (parseInt(num) <= parseInt(result)) {
                let show = `In line with budget`;
                this.setState({ "results": show });
                this.setState({ "staffNum": "The Results:" + num });
            } else {
                let show = `The rated budget has been exceeded`;
                this.setState({ "results": show });
                this.setState({ "staffNum": "The Results:" + num });
            }
        }

    }
    setScenario = (name, value) => {
        let Scenario = this.state.Scenario;
        if (name === 'unit') {
            Scenario.unit = value;
        } else if (name === 'HPPD') {
            Scenario.HPPD = value;
        } else if (name === 'census') {
            Scenario.census = value;
        } else if (name === 'bedUnit') {
            Scenario.bedUnit = value;
        }
        this.setState({ Scenario: Scenario });
        this.calculation(Scenario, this.state.num);
    }
    setScenarioStaffNum = (num) => {
        debugger;
        this.setState({ "num": num });
        this.calculation(this.state.Scenario, num);
    }


    render() {
        return (
            <div className="App">
                <Scenario props={this.state} setScenario={this.setScenario} />
                <StaffAdd staffs={this.state.staffs} results={this.state.results} staffNum={this.state.staffNum} setScenarioStaffNum={this.setScenarioStaffNum} />
            </div>
        );
    }
}

export default Main;
