import React,{Component} from 'react';
class StaffAdd extends Component
{
	
	  
	  handleAdd = (e) =>
	  {
		  let staff = {id:0,num:0,type:"",shift:""};
		  this.props.addStaff(staff);
	  }
	  handleChange = (e) =>
	  {
	  		  let staff = {id:0,num:0,type:"",shift:""};
	  		  this.props.addStaff(staff);
	  }
	  
	  
	  render () {
		  const {staffs} = this.props;
		   const {result} = this.props;
		const staffList = staffs.map((staff) =>
		    <div key={staff.id}>
		      <div>+</div> <div>-</div>
			  <div>type
			  <select>
			  <option>RN</option>
			  <option>LVN</option>
			   <option>Unlicensed</option>
			   </select>
			  </div>
			  <div>shift
			  <select>
			  <option>12 Hours Day</option>
			  <option>12 Hours Night</option>
			   <option>8 Hours Day</option>
			   <option>8 Hours Evening</option>
			   <option>8 Hours Night</option>
			   </select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleAdd}>Add new Staff</button></div>
				<div>{staffList}</div>
				<div>{result}</div>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
