import React,{Component} from 'react';
import StaffAdd from './StaffAdd'
import Info from './Info'

class App extends Component {
   state=
   {
	   title:"BSU In-Patient Nurse Staffing Simulator",
	   results:"23",
	   staffNum:"",
	   staffs: [],
	   info:{
	   	   unit:"",
	   	   HPPD:"",
	       bedUnit:"",
	       census:"",
	   		}
   }	
	
 calculation = (info,num) =>
 {
	
	if(info.bedUnit!=='' && info.census!=='' && info.HPPD!=='' && staffNum!=='')
	{
		let result = (info.bedUnit*(info.census/100))*info.HPPD/12;
		if(parseInt(num)<=parseInt(result))
		{
			let show = `In line with budget`;
		    this.setState({"results":show});
		   this.setState({"staffNum":"The Results:"+num});
		}else
		{
		     let show =`The rated budget has been exceeded`;
		     this.setState({"results":show});
		     this.setState({"staffNum":"The Results:"+num});
		}
	}
 	
 }
 setInfo = (name,value) =>
 {
 	let info=this.state.info;
	if(name==='unit')
	{
		info.unit=value;
	}else if(name==='HPPD')
	{
		info.HPPD=value;
	}else if(name==='census')
	{
		info.census=value;
	}else if(name==='bedUnit')
	{
		info.bedUnit=value;
	}
 	this.setState({info:info});
	this.calculation(info,this.state.staffNum);
 }
 setInfoStaffNum = (num) =>
 {
 	
 	this.setState({"num":num});
 	this.calculation(num,this.state.info);
 }

 
 render(){
   return (
     <div className="App">
		<Info props={this.state} setInfo={this.setInfo}/>
		<StaffAdd staffs={this.state.staffs} addStaff={this.addStaff} result={this.state.result} addNum={this.addNum} reduceNum={this.reduceNum}/>
     </div>
   );
  }
}

export default App;
