import React,{Component } from 'react';
import Typography from '@material-ui/core/Typography';
import Modal from '@material-ui/core/Modal';
import Button from '@material-ui/core/Button';
import Grid from '@material-ui/core/Grid';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { styled } from '@material-ui/styles';
class StaffAdd extends Component
{
	
	state=
	{
		   
		   staffs: [],
		   results:"",
		   open:false,
		   newStaff:
		   {
			num:1,type:"",shift:""   
		   },
		   style : {
		     position: 'absolute',
		     top: '50%',
		     left: '50%',
		     transform: 'translate(-50%, -50%)',
		     width: 400,
		     bgcolor: 'background.paper',
		     border: '2px solid #000',
		     boxShadow: 24,
		     p: 4,
		   }
	}
	 
	  Item = styled(Paper)(({ theme }) => ({
	   ...theme.typography.body2,
	   padding: theme.spacing(1),
	   textAlign: 'center',
	   color: theme.palette.text.secondary,
	 }));
	 
	  handleAdd = (e) =>
	  {
		  let staff = {id:Math.random(),num:1,type:"",shift:""};
		  let staffs=[...this.state.staffs,staff]
		  this.setState({staffs:staffs})
	  }
	 addNum = (id) =>
	 {
	 	 let staffs=this.state.staffs;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
	 		 if(staffs[i].id===id)
	 		 {
	 			 staffs[i].num++;
	 		 }
	 	 	
	 	 }
	 	 this.setState({staffs:staffs});
		 this.setResult(staffs);
	 }
	 reduceNum = (id) =>
	 {
		 
	 	 let staffs=this.state.staffs;
		 staffs.filter(item => item.id===id )
		 let isDelete=false;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
			 debugger;
	 		 if(staffs[i].id===id)
	 		 {
	 			 if(staffs[i].num>1)
	 			 {
	 				  staffs[i].num--;
					  
	 			 }else
				 {
					 	isDelete=true;  
				 }
	 			break;
	 		 }
	 	 	
	 	 }
		 if(isDelete)
		 {
			  staffs = staffs.filter(item => item.id!==id )
		 }
		 
	 	 this.setState({staffs:staffs});
		this.setResult(staffs);
	 }
	
	setResult = (staffs) =>
	{
			 
		 let num = 0;
		 for(var i=0;i<staffs.length;i++)
		 {
			
			num = num+staffs[i].num;
			
		 }
		 const result = this.props.result;
		 if(result===''){
			 return;
		 }
		 if(num<=result)
		 {
			 let show = num+"\n\r"+"In line with budget";
			 this.setState({"results":show});
		 }else
		 {
			 let show = num+"\n\r"+"The rated budget has been exceeded";
			 this.setState({"results":show});
		 }
		
	}
	

	
	handleOpen = () =>
	{
			 
		this.setState({"open":true});	
	}
	handleClose = () =>
	{
			 
		this.setState({"open":false});	
	}
	// const [open, setOpen] = React.useState(false);
	
	  render () {
		const staffList = this.state.staffs.map((staff) =>
		    <div key={staff.id}>
		      <div onClick={() => this.addNum(staff.id)} >+</div> <div  onClick={() => this.reduceNum(staff.id)}>-</div><input value={staff.num} />
			  <div>type
			  <select>
				<option>RN</option>
				<option>LVN</option>
			   <option>Unlicensed</option>
			   </select>
			  </div>
			  <div>shift
			  <select>
			  <option>12 Hours Day</option>
			  <option>12 Hours Night</option>
			   <option>8 Hours Day</option>
			   <option>8 Hours Evening</option>
			   <option>8 Hours Night</option>
			   </select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleOpen}>Add new Staff</button></div>
				<div>{staffList}</div>
				<div>{this.state.results}</div>
				
				
				
				
				
				<Modal
				        open={this.state.open}
				        onClose={this.handleClose}
				        aria-labelledby="modal-modal-title"
				        aria-describedby="modal-modal-description"
				      >
				   
				          <Typography id="modal-modal-title" variant="h6" component="h2">
				            Select your stuffing memeber close
				          </Typography>
						  <Grid container spacing={2}>
						    <Grid item xs={4}>
						       <InputLabel id="type">Type</InputLabel>
						    </Grid>
						    <Grid item xs={8}>
						     <Select labelId="label" id="type" value="RN">
						       <MenuItem value="RN">RN</MenuItem>
						       <MenuItem value="LVN">LVN</MenuItem>
						     	 <MenuItem value="Unlicensed">Unlicensed</MenuItem>
						     </Select>
						    </Grid>
						    <Grid item xs={4}>
						      <this.Item><InputLabel htmlFor="my-input">Quantity</InputLabel></this.Item>
						    </Grid>
						    <Grid item xs={8}>
						      <Item> <Input type="number" readonly="true" id="my-input"/></Item>
						    </Grid>
						    <Grid item xs={4}>
						      <Item> <InputLabel id="type">Shift</InputLabel></Item>
						    </Grid>
						    <Grid item xs={8}>
						      <Item>  
						  	<Select labelId="label" id="Shift" value="12 Hours Day">
						  		    <MenuItem value="12 Hours Day">12 Hours Day</MenuItem>
						  		    <MenuItem value="12 Hours Night">12 Hours Night</MenuItem>
						  		  	<MenuItem value="8 Hours Day">8 Hours Day</MenuItem>
						  			<MenuItem value="8 Hours Evening">8 Hours Evening</MenuItem>
						  			<MenuItem value="8 Hours Night">8 Hours Night</MenuItem>
						  	</Select>
						  	</Item>
						  	
						    </Grid>
						     <Button variant="contained">Submit</Button>
						  </Grid>
				</Modal>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
