import React, { Component } from 'react';
import Modal from './UI/Modal'
import './App.css';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      displayModal: false
    }
  }

  modalOpen = () => {
    this.setState({ displayModal: true });
  };

  modalClose = () => {
    this.setState({ displayModal: false });
  };

  render() {
      return (


          <div className="App">
              <header className="App-header">
                  <h1>BSU In-Patient Nurse Staffing Simulator</h1>

                  <p class="lead">
                      Hospital unit is <input
                          type='text'
                          name='HospitalUnit'

                      /> and HPPD is <input
                          type='text'
                          name='HPPD'

                      />. You census is <input
                          type='text'
                          name='CensusPercentage'

                      />% full in a <input
                          type='text'
                          name='BedUnit'

                      /> bed unit. Allocate you resources accordingly.
            </p>

                  <button onclick="AtHomePractice()">At Home Practice</button>

              </header>
         


     
        <a onClick={this.modalOpen} class="btn-floating btn-large waves-effect waves-light red"><i class="material-icons">Add New Stuff</i></a>
        <Modal
          show={this.state.displayModal}
          modalClosed={this.modalClose}
        >
          <React.Fragment>
            <h3>Select your stuffing member choise</h3>
                      <p>Type: </p>
                      <div>
                          <select
                              value={this.state.selectValue}
                              onChange={this.handleChange}
                          >
                              <option value="RN">RN</option>
                              <option value="LVN">LVN</option>
                              <option value="Unlicensed">Unlicensed</option>
                          </select>
                          <p>{message}</p>
                      </div>  

            <button class="btn waves-effect waves-light" onClick={this.modalClose}>Submit</button>
          </React.Fragment>
        </Modal>
      </div>
    );
  }
}

export default App;
