import React,{Component} from 'react';
class StaffAdd extends Component
{
	
	state=
	{
		   title:"BSU In-Patient Nurse Staffing Simulator",
		   result:"23",
		   staffs: [],
		   info:{
		   	   unit:"",
		   	   HPPD:"",
		       bedUnit:"",
		       census:"",
		   		}
	}
	  
	  handleAdd = (e) =>
	  {
		  let staff = {id:0,num:0,type:"",shift:""};
		  this.props.addStaff(staff);
	  }
	  handleChange = (e) =>
	  {
	  		  let staff = {id:0,num:0,type:"",shift:""};
	  		  this.props.addStaff(staff);
	  }
	  addNum = (id) =>
	  {
	  		  debugger;
	  		  this.props.addNum(id);
	  }
	  reduceNum = (id) =>
	  {
	  		  
	  		  this.props.reduceNum(id);
	  }
	  render () {
		  const {staffs} = this.props;
		   const {result} = this.props;
		const staffList = staffs.map((staff) =>
		    <div key={staff.id}>
		      <div onClick={this.addNum(staff.id)}>+</div> <div onClick={this.reduceNum(staff.id)}>-</div><input  />
			  <div>type
			  <select>
				<option>RN</option>
				<option>LVN</option>
			   <option>Unlicensed</option>
			   </select>
			  </div>
			  <div>shift
			  <select>
			  <option>12 Hours Day</option>
			  <option>12 Hours Night</option>
			   <option>8 Hours Day</option>
			   <option>8 Hours Evening</option>
			   <option>8 Hours Night</option>
			   </select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleAdd}>Add new Staff</button></div>
				<div>{staffList}</div>
				<div>{result}</div>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
