import React,{Component} from 'react';
import StaffAdd from './StaffAdd'
import Info from './Info'

class App extends Component {
   state=
   {
	   title:"BSU In-Patient Nurse Staffing Simulator",
	   result:"23",
	   staffs: [],
	   info:{
	   	   unit:"",
	   	   HPPD:"",
	       bedUnit:"",
	       census:"",
	   		}
   }	
 addStaff = (staff) =>
 {
	 let staffs=[...this.state.staffs,staff]
	 this.calculation(staffs);
	this.setState({staffs:staffs})
 }	
 calculation = (staffs) =>
 {
	
	if(this.state.info.bedUnit!=='' && this.state.info.census!=='' && this.state.info.HPPD!=='')
	{
		let result = (this.state.info.bedUnit*(this.state.info.census/100))*this.state.info.HPPD/12;
		var num = 0;
		for(var i=0;i<staff.length;i++)
		{
			num = num+staff[i].num;
		}
		 this.setState({"result":result})
	}
 	
 }
 setInfo = (name,value) =>
 {
 	let info=this.state.info;
	if(name==='unit')
	{
		info.unit=value;
	}else if(name==='HPPD')
	{
		info.HPPD=value;
	}else if(name==='census')
	{
		info.census=value;
	}else if(name==='bedUnit')
	{
		info.bedUnit=value;
	}
 	this.setState({"bedUnit":info})
 }
 addNum = (id) =>
 {
 	 let staffs=this.state.staffs;
	 for(var i=0;i<staff.length;i++)
	 {
		 if(staff[i].id==id)
		 {
			 staff[i].num++;
		 }
	 	
	 }
	 this.setState({staffs:staffs})
 }
 reduceNum = (id) =>
 {
 	 let staffs=this.state.staffs;
 	 for(var i=0;i<staff.length;i++)
 	 {
 		 if(staff[i].id==id)
 		 {
			 if(staff[i].num>0)
			 {
				  staff[i].num--;
			 }
 			
 		 }
 	 	
 	 }
 	 this.setState({staffs:staffs})
 }
 
 render(){
   return (
     <div className="App">
		<Info props={this.state} setInfo={this.setInfo}/>
		<StaffAdd staffs={this.state.staffs} addStaff={this.addStaff} result={this.state.result} addNum={addNum} addNum={addNum}/>
     </div>
   );
  }
}

export default App;
