import React,{Component} from 'react';
import './index.css';
class Info extends Component
{
	 
		state=
		{
			   title:"BSU In-Patient Nurse Staffing Simulator",
			   unit:"",
			   HPPD:"",
			   bedUnit:"",
			   census:""
		}
		handleAdd = (e) =>
		{
			const inputName = e.target.name;
			const value = e.target.value;
			if(!(/^\+?[1-9][0-9]*$/ .test(value)))
			{
				alert("只能输入正整数")
			}
		}
	  render () {
	
	    return (
		    <div>
			   <h1 >{this.state.title}</h1>
			   <div>
			    Hostpital unit is <input name="unit" id="unit" value={this.state.unit} onChange={this.handleAdd}/> and HPPD is <input value={this.state.HPPD} name="HPPD" id="HPPD"/>
			   </div>
			   <div>
			   you census is <input name="census" value={this.state.census} id="census"/>% full in a  <input name="bedUnit" value={this.state.bedUnit} id="bedUnit"/> bed unit
			   </div>
			   <div>
			  Allocate your resources accordlingly
			   </div>
			   <div>
			    <button >Add Home Practice</button>
			    </div>
            </div>
		);
	  }
			
		
	
}
export default Info
