import React,{Component} from 'react';
import StaffAdd from './StaffAdd'
import Info from './Info'

class App extends Component {
   state=
   {
	   title:"BSU In-Patient Nurse Staffing Simulator",
	   result:"23",
	   staffs: [],
	   info:{
	   	   unit:"",
	   	   HPPD:"",
	       bedUnit:"",
	       census:"",
	   		}
   }	
 addStaff = (staff) =>
 {
	 let staffs=[...this.state.staffs,staff]
	 this.calculation(staffs);
	this.setState({staffs:staffs})
 }	
 calculation = (staffs) =>
 {
	debugger;
	if(this.state.info.bedUnit!=='' && this.state.info.census!=='' && this.state.info.HPPD!=='')
	{
		let result = this.state.bedUnit*(this.state.census/100)*this.state.HPPD/12;
		 this.setState({"result":result})
	}
 	
 }
 setInfo = (name,value) =>
 {
 	let info=this.state.info;
	if(name==='unit')
	{
		info.unit=value;
	}else if(name==='HPPD')
	{
		info.HPPD=value;
	}else if(name==='census')
	{
		info.census=value;
	}else if(name==='bedUnit')
	{
		info.bedUnit=value;
	}
 	this.setState({"bedUnit":info})
 }
 render(){
   return (
     <div className="App">
		<Info props={this.state}/>
		<StaffAdd staffs={this.state.staffs} addStaff={this.addStaff} result={this.state.result}/>
     </div>
   );
  }
}

export default App;
