import React,{Component} from 'react';
class StaffAdd extends Component
{
	
	  
	  handleAdd = (e) =>
	  {
		  let staff = {id:0,num:0,type:"",shift:""};
		  this.props.addStaff(staff);
	  }
	  
	  render () {
		  const {staffs} = this.props;
		const staffList = staffs.map((staff) =>
		    <div key={staff.id}>
		      <div>+</div> <div>-</div>
			  <div>type
			  <select></select>
			  </div>
			  <div>shift
			  <select></select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleAdd}>Add new Staff</button></div>
				<div>{staffList}</div>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
