import React,{Component} from 'react';
class StaffAdd extends Component
{
	
	state=
	{
		   
		   staffs: [],
		   results:""
	}
	  
	  handleAdd = (e) =>
	  {
		  let staff = {id:Math.random(),num:1,type:"",shift:""};
		  let staffs=[...this.state.staffs,staff]
		  this.setState({staffs:staffs})
	  }
	 addNum = (id) =>
	 {
	 	 let staffs=this.state.staffs;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
	 		 if(staffs[i].id===id)
	 		 {
	 			 staffs[i].num++;
	 		 }
	 	 	
	 	 }
	 	 this.setState({staffs:staffs});
		 this.setResult(staffs);
	 }
	 reduceNum = (id) =>
	 {
		 
	 	 let staffs=this.state.staffs;
		 staffs.filter(item => item.id===id )
		 let isDelete=false;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
			 debugger;
	 		 if(staffs[i].id===id)
	 		 {
	 			 if(staffs[i].num>1)
	 			 {
	 				  staffs[i].num--;
					  
	 			 }else
				 {
					 	isDelete=true;  
				 }
	 			break;
	 		 }
	 	 	
	 	 }
		 if(isDelete)
		 {
			  staffs = staffs.filter(item => item.id!==id )
		 }
		 
	 	 this.setState({staffs:staffs});
		this.setResult(staffs);
	 }
	
	setResult = (staffs) =>
	{
			 
		 let num = 0;
		 for(var i=0;i<staffs.length;i++)
		 {
			
			num = num+staffs[i].num;
			
		 }
		 const result = this.props.result;
		 if(result===''){
			 return;
		 }
		 if(num<=result)
		 {
			 let show = result+"\n\r"+"In line with budget";
			 this.setState({"results":show});
		 }else
		 {
			 let show = result+"\n\r"+"The rated budget has been exceeded";
			 this.setState({"results":show});
		 }
		
	}
	
	  render () {
		const staffList = this.state.staffs.map((staff) =>
		    <div key={staff.id}>
		      <div onClick={() => this.addNum(staff.id)} >+</div> <div  onClick={() => this.reduceNum(staff.id)}>-</div><input value={staff.num} />
			  <div>type
			  <select>
				<option>RN</option>
				<option>LVN</option>
			   <option>Unlicensed</option>
			   </select>
			  </div>
			  <div>shift
			  <select>
			  <option>12 Hours Day</option>
			  <option>12 Hours Night</option>
			   <option>8 Hours Day</option>
			   <option>8 Hours Evening</option>
			   <option>8 Hours Night</option>
			   </select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleAdd}>Add new Staff</button></div>
				<div>{staffList}</div>
				<div>{this.state.results}</div>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
