import React,{Component } from 'react';
import { Modal,Form, Input, Button, Checkbox, Select, InputNumber,Row, Col} from 'antd';
import 'antd/dist/antd.css'
class StaffAdd extends Component
{
	
	state=
	{
		   
		   staffs: [],
		   results:"",
		   open:false,
		   
		   style : {
		     position: 'absolute',
		     top: '50%',
		     left: '50%',
		     transform: 'translate(-50%, -50%)',
		     width: 400,
		     bgcolor: 'background.paper',
		     border: '2px solid #000',
		     boxShadow: 24,
		     p: 4,
		   }
	}
	
	 
	 
	  handleAdd = (value) =>
	  {
		  
		  let staff = {id:Math.random(),num:value.quantity,type:value.type,shift:value.shift};
		  let staffs=[...this.state.staffs,staff]
		  this.setState({staffs:staffs});
		  this.handleClose();
	  }
	 addNum = (id) =>
	 {
	 	 let staffs=this.state.staffs;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
	 		 if(staffs[i].id===id)
	 		 {
	 			 staffs[i].num++;
	 		 }
	 	 	
	 	 }
	 	 this.setState({staffs:staffs});
		 this.setResult(staffs);
	 }
	 reduceNum = (id) =>
	 {
		 
	 	 let staffs=this.state.staffs;
		 staffs.filter(item => item.id===id )
		 let isDelete=false;
	 	 for(var i=0;i<staffs.length;i++)
	 	 {
			 debugger;
	 		 if(staffs[i].id===id)
	 		 {
	 			 if(staffs[i].num>1)
	 			 {
	 				  staffs[i].num--;
					  
	 			 }else
				 {
					 	isDelete=true;  
				 }
	 			break;
	 		 }
	 	 	
	 	 }
		 if(isDelete)
		 {
			  staffs = staffs.filter(item => item.id!==id )
		 }
		 
	 	 this.setState({staffs:staffs});
		this.setResult(staffs);
	 }
	
	setResult = (staffs) =>
	{
			 
		 let num = 0;
		 for(var i=0;i<staffs.length;i++)
		 {
			
			num = num+staffs[i].num;
			
		 }
		 const result = this.props.result;
		 if(result===''){
			 return;
		 }
		 if(num<=result)
		 {
			 let show = num+"\n\r"+"In line with budget";
			 this.setState({"results":show});
		 }else
		 {
			 let show = num+"\n\r"+"The rated budget has been exceeded";
			 this.setState({"results":show});
		 }
		
	}
	

	
	handleOpen = () =>
	{
			 
		this.setState({"open":true});	
	}
	handleClose = () =>
	{
			 
		this.setState({"open":false});	
	}
	// const [open, setOpen] = React.useState(false);
	
	  render () {
		const staffList = this.state.staffs.map((staff) =>
		    
			<div key={staff.id}>
			
		      <div onClick={() => this.addNum(staff.id)} >+</div> <div  onClick={() => this.reduceNum(staff.id)}>-</div><input value={staff.num} />
			  <div>type
			  <select>
				<option>RN</option>
				<option>LVN</option>
			   <option>Unlicensed</option>
			   </select>
			  </div>
			  <div>shift
			  <select>
			  <option>12 Hours Day</option>
			  <option>12 Hours Night</option>
			   <option>8 Hours Day</option>
			   <option>8 Hours Evening</option>
			   <option>8 Hours Night</option>
			   </select>
			  </div>
		    </div>
		  );
		 
	    return (
		     <div>
				<div><button onClick={this.handleOpen}>Add new Staff</button></div>
				<div>{staffList}</div>
				<div>{this.state.results}</div>
				
				
				
				
				
				<Modal
				       title="Select your stuffing memeber close" visible={this.state.open} onOk={this.handleOpen} onCancel={this.handleClose}
					    footer={null}
				      >
				    <Form
				           labelCol={{ span: 6 }}
				           wrapperCol={{ span: 12 }}
				           layout="horizontal"
						   labelAlign="left"
						   onFinish={this.handleAdd}
				         >
						 <Form.Item label="Type" name="type"
						  rules={[{required: true}]}
						 >
						     <Select>
						        <Select.Option value="RN">RN</Select.Option>
								<Select.Option value="LVN">LVN</Select.Option>
								<Select.Option value="Unlicensed">Unlicensed</Select.Option>
						     </Select>
						 </Form.Item>
						 <Form.Item label="Quantity" name="quantity" defaultValue='1' rules={[{required: true}]}>
						        <InputNumber   min="1"/>
						 </Form.Item>
						 <Form.Item label="Shift"  name="shift"
						 rules={[{required: true}]}
						 >
						    <Select >
						        <Select.Option value="12 Hours Day">12 Hours Day</Select.Option>
						 		<Select.Option value="12 Hours Night">12 Hours Night</Select.Option>
						 		<Select.Option value="8 Hours Day">8 Hours Day</Select.Option>
								<Select.Option value="8 Hours Evening">8 Hours Evening</Select.Option>
								<Select.Option value="8 Hours Night">8 Hours Night</Select.Option>
						    </Select>
						 </Form.Item>
						 <Form.Item wrapperCol={{ offset: 0, span: 16 }} >
						        <Button type="primary" htmlType="submit">
						          Submit
						        </Button>
						 </Form.Item>
						 
					</Form>
				         
					 

				</Modal>
			  </div>
		);
	  }
			
		
	
}
export default StaffAdd
